import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  otherDataForm: FormGroup;
  horizontalDistancesControls: FormControl[] = [];
  verticalDistancesControls: FormControl[] = [];

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit() {
    this.otherDataForm = this.formBuilder.group({
      horizontalLineCount: [0, Validators.min(0)],
      horizontalDistances: this.formBuilder.array([]),
      verticalLineCount: [0, Validators.min(0)],
      verticalDistances: this.formBuilder.array([]),
    });
  }

  onSubmit() {
    // İşlemleriniz burada gerçekleştirilir
  }

  getLinesArray(lineCount: number): number[] {
    return Array(lineCount).fill(0).map((_, index) => index);
  }
}
<div style="display: flex; justify-content: flex-end; align-items: center; height: 100vh;">
  <div class="col-md-4">
    <form (ngSubmit)="onSubmit()" [formGroup]="otherDataForm" class="form-group mb-3">
      <label for="horizontalLineCount" class="mr-2">Yatayda kaç adet çizgi olsun:</label>
      <input type="number" id="horizontalLineCount" name="horizontalLineCount" class="form-control form-control-sm" formControlName="horizontalLineCount" />

      <ng-container *ngIf="otherDataForm.get('horizontalLineCount')?.value > 0">
        <ng-container *ngFor="let horizontalLine of horizontalDistancesControls; let hIndex = index">
          <label>
            Yataydaki {{ hIndex + 1 }}. çizginin
            {{ hIndex === 0 ? 'dikdörtgenin sol çizgisi ile' : (hIndex + 1) + '. çizgi ile' }}
            arasındaki mesafe kaç mm olmalı:
          </label>
          <input type="number" [formControl]="horizontalLine" class="form-control form-control-sm" />
        </ng-container>
      </ng-container>

      <label for="verticalLineCount" class="mr-2">Dikeyde kaç adet çizgi olsun:</label>
      <input type="number" id="verticalLineCount" name="verticalLineCount" class="form-control form-control-sm" formControlName="verticalLineCount" />

      <ng-container *ngIf="otherDataForm.get('verticalLineCount')?.value > 0">
        <ng-container *ngFor="let verticalLine of verticalDistancesControls; let vIndex = index">
          <label>
            Dikeydeki {{ vIndex + 1 }}. çizginin
            {{ vIndex === 0 ? 'dikdörtgenin üst çizgisi ile' : (vIndex + 1) + '. çizgi ile' }}
            arasındaki mesafe kaç mm olmalı:
          </label>
          <input type="number" [formControl]="verticalLine" class="form-control form-control-sm" />
        </ng-container>
      </ng-container>

      <button type="submit" class="btn btn-primary mt-3">Gönder</button>
    </form>
  </div>
</div>
