import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  @ViewChild('canvas') canvas!: ElementRef<HTMLCanvasElement>;
  rectangleForm: FormGroup;

  constructor(private formBuilder: FormBuilder) {
    this.rectangleForm = this.formBuilder.group({
      width: [null],
      height: [null],
      horizontalLines: [null],
      verticalLines: [null],
      horizontalDistances: this.formBuilder.group({}),
      verticalDistances: this.formBuilder.group({}),
    });
  }

  ngOnInit(): void {
    this.rectangleForm.get('width')?.valueChanges.subscribe(() => {
      this.drawRectangle();
    });

    this.rectangleForm.get('height')?.valueChanges.subscribe(() => {
      this.drawRectangle();
    });

    this.rectangleForm.get('horizontalLines')?.valueChanges.subscribe((value) => {
      this.addHorizontalDistances(value);
    });

    this.rectangleForm.get('verticalLines')?.valueChanges.subscribe((value) => {
      this.addVerticalDistances(value);
    });
  }

  ngAfterViewInit(): void {
    const canvas = this.canvas.nativeElement;
    canvas.width = 800;
    canvas.height = 600;
  }

  onSubmit(): void {
    const horizontalDistances = this.rectangleForm.value.horizontalDistances;
    const verticalDistances = this.rectangleForm.value.verticalDistances;

    console.log('Horizontal Distances:', horizontalDistances);
    console.log('Vertical Distances:', verticalDistances);

    this.rectangleForm.reset();
  }

  getLinesArray(lineCount: number): number[] {
    return Array(lineCount)
      .fill(0)
      .map((_, index) => index + 1);
  }

  addHorizontalDistances(count: number): void {
    const horizontalDistances = this.rectangleForm.get('horizontalDistances') as FormGroup;
    horizontalDistances.reset();
    for (let i = 0; i < count; i++) {
      horizontalDistances.addControl(`distance_${i}`, this.formBuilder.control(null));
    }
  }

  addVerticalDistances(count: number): void {
    const verticalDistances = this.rectangleForm.get('verticalDistances') as FormGroup;
    verticalDistances.reset();
    for (let i = 0; i < count; i++) {
      verticalDistances.addControl(`distance_${i}`, this.formBuilder.control(null));
    }
  }

  drawRectangle(): void {
    const width = this.rectangleForm.value.width;
    const height = this.rectangleForm.value.height;
    const horizontalDistances = this.rectangleForm.value.horizontalDistances;
    const verticalDistances = this.rectangleForm.value.verticalDistances;

    if (!width || !height) return;

    const canvas = this.canvas.nativeElement;
    const ctx = canvas.getContext('2d');

    if (!ctx) return;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const offsetX = (canvas.width - width) / 2;
    const offsetY = (canvas.height - height) / 2;

    ctx.beginPath();
    ctx.rect(10 + offsetX, 10 + offsetY, width, height);
    ctx.stroke();

    ctx.font = '16px sans-serif';
    ctx.fillText(`Width: ${width}mm`, width + 20 + offsetX, height / 2 + offsetY);
    ctx.fillText(`Height: ${height}mm`, width / 2 + offsetX, height + 30 + offsetY);

    let currentY = 10 + offsetY;
    for (const key in horizontalDistances) {
      const distance = horizontalDistances[key];
      if (distance !== null) {
        currentY += distance;
        const isBeyondBounds = currentY > height + 10 + offsetY;

        ctx.beginPath();
        ctx.moveTo(10 + offsetX, currentY);
        ctx.lineTo(width + 10 + offsetX, currentY);
        ctx.strokeStyle = isBeyondBounds ? 'red' : 'black';
        ctx.stroke();

        const textWidth = ctx.measureText(`${distance}mm`).width;
        ctx.fillStyle = isBeyondBounds ? 'red' : 'black';
        ctx.fillText(`${distance}mm`, width + Math.max(20, textWidth) + offsetX, currentY);
      }
    }

    let currentX = 10 + offsetX;
    for (const key in verticalDistances) {
      const distance = verticalDistances[key];
      if (distance !== null) {
        currentX += distance;
        const isBeyondBounds = currentX > width + 10 + offsetX;

        ctx.beginPath();
        ctx.moveTo(currentX, 10 + offsetY);
        ctx.lineTo(currentX, height + 10 + offsetY);
        ctx.strokeStyle = isBeyondBounds ? 'red' : 'black';
        ctx.stroke();

        const textWidth = ctx.measureText(`${distance}mm`).width;
        ctx.fillStyle = isBeyondBounds ? 'red' : 'black';
        ctx.fillText(`${distance}mm`, currentX - textWidth / 2, height + Math.max(30, textWidth) + offsetY);
      }
    }
  }
}
