import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
 selector: 'app-home',
 templateUrl: './home.component.html',
 styleUrls: ['./home.component.scss']
})
export class HomeComponent {

 width!: number;
 height!: number;
 hgap!: number; // Bu satırı ekledim
 vgap!: number; // Bu satırı ekledim
 hlines!: number; // Bu satırı ekledim
 vlines!: number; // Bu satırı ekledim

 constructor(private http: HttpClient) { }

 onSubmit() {
 // Dikdörtgeni ve çizgileri çizen fonksiyonu çağır
 this.drawRectangle(this.width, this.height, this.hlines, this.vlines, this.hgap, this.vgap); // Parametreleri değiştirdim

 // Server'a gönderilecek verileri oluştur
 let data = {
 width: this.width,
 height: this.height,
 hgap: this.hgap, // Bu satırı ekledim
 vgap: this.vgap, // Bu satırı ekledim
 hlines: this.hlines, // Bu satırı ekledim
 vlines: this.vlines // Bu satırı ekledim
 };

 // Server'a göndermek için HTTP servisini kullan
 this.http.post('server_url', data).subscribe(
 response => {
 // Server'dan gelen yanıtı işle
 console.log(response);
 },
 error => {
 // Hata durumunda mesaj göster
 console.error(error);
 }
 );
 }

 drawRectangle(width: number, height: number, hlines: number, vlines: number, hgap: number, vgap: number) { // Parametreleri değiştirdim
 // Bir canvas elementi oluştur
 let canvas = document.createElement('canvas');
 canvas.width = width;
 canvas.height = height;

 // Canvas'ın context'ini al
 let ctx = canvas.getContext('2d');

 // Dikdörtgeni çiz
 ctx?.strokeRect(0, 0, width, height);

 // Yatay çizgileri çiz
 for (let i = 1; i <= hlines; i++) {
  let y = i * hgap;
  ctx?.moveTo(0, y);
  ctx?.lineTo(width, y);
  ctx?.stroke();
 }

 // Dikey çizgileri çiz
 for (let j = 1; j <= vlines; j++) {
  let x = j * vgap;
  ctx?.moveTo(x, 0);
  ctx?.lineTo(x, height);
  ctx?.stroke();
 }

 // Canvas'ı sayfaya ekle
 document.body.appendChild(canvas);
 }
}
<div style="display: flex; justify-content: flex-end; align-items: center; height: 100vh;">
  <div class="col-md-4">
  <form (ngSubmit)="onSubmit()" class="form-group mb-3">
  <label for="width" class="mr-2">Dikdörtgenin genişliği (mm):</label>
  <input type="number" id="width" name="width" class="form-control form-control-sm" [(ngModel)]="width">

  <label for="height" class="mr-2">Dikdörtgenin yüksekliği (mm):</label>
  <input type="number" id="height" name="height" class="form-control form-control-sm" [(ngModel)]="height">

  <label for="hlines" class="mr-2">Yatayda kaç çizgi istiyorsunuz?</label>
  <input type="number" id="hlines" name="hlines" class="form-control form-control-sm" [(ngModel)]="hlines">

  <label for="hgap" class="mr-2">Yatay çizgiler arasındaki uzaklık (mm):</label>
  <input type="number" id="hgap" name="hgap" class="form-control form-control-sm" [(ngModel)]="hgap">

  <label for="vlines" class="mr-2">Dikeyde kaç çizgi istiyorsunuz?</label>
  <input type="number" id="vlines" name="vlines" class="form-control form-control-sm" [(ngModel)]="vlines">

  <label for="vgap" class="mr-2">Dikey çizgiler arasındaki uzaklık (mm):</label>
  <input type="number" id="vgap" name="vgap" class="form-control form-control-sm" [(ngModel)]="vgap">

  <button type="submit" class="btn btn-primary">Gönder</button>
  </form>
  </div>
 </div>
