import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Chart, ChartDataset, ChartOptions } from 'chart.js';

@Component({
  selector: 'app-home',
  template: `
    <div style="display: flex; justify-content: space-between; align-items: center; height: 100vh;">
      <div class="col-md-4">
        <canvas #canvas></canvas>
      </div>
      <div class="col-md-4">
        <form [formGroup]="otherDataForm" (ngSubmit)="onSubmit()" class="form-group mb-3">
          <!-- Form fields... -->

          <button type="submit" class="btn btn-primary">Gönder</button>
        </form>
      </div>
    </div>
  `,
})
export class HomeComponent implements OnInit {
  @ViewChild('canvas', { static: true }) canvas!: ElementRef<HTMLCanvasElement>;
  otherDataForm!: FormGroup;
  ctx!: CanvasRenderingContext2D;

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.otherDataForm = this.formBuilder.group({
      width: null,
      height: null,
      horizontalLines: null,
      verticalLines: null,
      horizontalDistances: this.formBuilder.group({}),
      verticalDistances: this.formBuilder.group({}),
    });

    // Subscribe to valueChanges observable of horizontalLines form control
    this.otherDataForm.get('horizontalLines')?.valueChanges.subscribe((value) => {
      this.addHorizontalDistances(value);
    });

    // Subscribe to valueChanges observable of verticalLines form control
    this.otherDataForm.get('verticalLines')?.valueChanges.subscribe((value) => {
      this.addVerticalDistances(value);
    });

    // Subscribe to valueChanges observable of otherDataForm and call drawRectangle() method when any value changes.
    this.otherDataForm.valueChanges.subscribe(() => {
      this.drawRectangle();
    });
  }

  ngAfterViewInit(): void {
    // Set initial size of canvas element.
    const canvas = this.canvas.nativeElement;
    this.ctx = canvas.getContext('2d')!;
    canvas.width = 800;
    canvas.height = 600;
  }

  onSubmit(): void {
    const horizontalDistances = this.otherDataForm.value.horizontalDistances;
    const verticalDistances = this.otherDataForm.value.verticalDistances;

    console.log('Horizontal Distances:', horizontalDistances);
    console.log('Vertical Distances:', verticalDistances);

    this.ctx.clearRect(0, 0, this.ctx.canvas.width, this.ctx.canvas.height);

    const width = this.otherDataForm.value.width;
    const height = this.otherDataForm.value.height;

    this.drawRectangle(width, height);
  }

  addHorizontalDistances(count: number): void {
    const horizontalDistancesForm = this.otherDataForm.get('horizontalDistances');

    if (horizontalDistancesForm) {
      const currentCount = Object.keys(horizontalDistancesForm.value).length;

      if (count > currentCount) {
        for (let i = currentCount + 1; i <= count; i++) {
          horizontalDistancesForm.addControl(`distance${i}`, this.formBuilder.control(null));
        }
      } else if (count < currentCount) {
        for (let i = currentCount; i > count; i--) {
          horizontalDistancesForm.removeControl(`distance${i}`);
        }
      }
    }
  }

  addVerticalDistances(count: number): void {
    const verticalDistancesForm = this.otherDataForm.get('verticalDistances');

    if (verticalDistancesForm) {
      const currentCount = Object.keys(verticalDistancesForm.value).length;

      if (count > currentCount) {
        for (let i = currentCount + 1; i <= count; i++) {
          verticalDistancesForm.addControl(`distance${i}`, this.formBuilder.control(null));
        }
      } else if (count < currentCount) {
        for (let i = currentCount; i > count; i--) {
          verticalDistancesForm.removeControl(`distance${i}`);
        }
      }
    }
  }

  drawRectangle(): void {
    const width = this.otherDataForm.value.width;
    const height = this.otherDataForm.value.height;

    this.ctx.clearRect(0, 0, this.ctx.canvas.width, this.ctx.canvas.height);
    this.ctx.strokeStyle = 'blue';
    this.ctx.lineWidth = 2;

    this.ctx.beginPath();
    this.ctx.moveTo(0, 0);
    this.ctx.lineTo(width, 0);
    this.ctx.lineTo(width, height);
    this.ctx.lineTo(0, height);
    this.ctx.closePath();
    this.ctx.stroke();
  }
}
