import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormGroup, FormArray, FormControl, Validators } from '@angular/forms';

@Component({
 selector: 'app-home',
 templateUrl: './home.component.html',
 styleUrls: ['./home.component.scss']
})
export class HomeComponent {

 width!: number;
 height!: number;
 hlines!: number;
 vlines!: number;

 // Dinamik form alanları için bir form group oluştur
 otherDataForm = new FormGroup({});

 constructor(private http: HttpClient) { }

 onSubmit() {
 // Yatay çizgiler için bir form array oluştur
 this.otherDataForm.addControl('hlines', new FormArray([]));

 // Kullanıcıdan alınan değeri hlines değişkenine ata
 let hlines = this.otherDataForm.get('hlines').value;

 // hlines sayısı kadar giriş alanı oluştur
 for (let i = 0; i < hlines; i++) {
  // Her giriş alanı için bir form control oluştur
  let hgap = new FormControl('', Validators.required);

  // Form array'e form control'u ekle
  (this.otherDataForm.get('hlines') as FormArray).push(hgap);
 }

 // Dikey çizgiler için bir form array oluştur
 this.otherDataForm.addControl('vlines', new FormArray([]));

 // Kullanıcıdan alınan değeri vlines değişkenine ata
 let vlines = this.otherDataForm.get('vlines').value;

 // vlines sayısı kadar giriş alanı oluştur
 for (let j = 0; j < vlines; j++) {
  // Her giriş alanı için bir form control oluştur
  let vgap = new FormControl('', Validators.required);

  // Form array'e form control'u ekle
  (this.otherDataForm.get('vlines') as FormArray).push(vgap);
 }

 // Dikdörtgeni ve çizgileri çizen fonksiyonu çağır
 this.drawRectangle(this.width, this.height, this.hlines, this.vlines);

 // Server'a gönderilecek verileri oluştur
 let data = {
 width: this.width,
 height: this.height,
 hgap: this.otherDataForm.get('hlines').value, // Yatay aralıkları bir array olarak al
 vgap: this.otherDataForm.get('vlines').value, // Dikey aralıkları bir array olarak al
 };

 // Server'a göndermek için HTTP servisini kullan
 this.http.post('server_url', data).subscribe(
 response => {
 // Server'dan gelen yanıtı işle
 console.log(response);
 },
 error => {
 // Hata durumunda mesaj göster
 console.error(error);
 }
 );
 }

 drawRectangle(width: number, height: number, hlines: number, vlines: number) {
 // Bir canvas elementi oluştur
 let canvas = document.createElement('canvas');
 canvas.width = width;
 canvas.height = height;

 // Canvas'ın context'ini al
 let ctx = canvas.getContext('2d');

 // Dikdörtgeni çiz
 ctx?.strokeRect(0, 0, width, height);

 // Yatay çizgileri çiz
 for (let i = 0; i < hlines; i++) {
  // Her yatay çizgi için aralığı form array'den al
  let y = this.otherDataForm.get('hlines').controls[i].value;
  ctx?.moveTo(0, y);
  ctx?.lineTo(width, y);
  ctx?.stroke();
 }

 // Dikey çizgileri çiz
 for (let j = 0; j < vlines; j++) {
  // Her dikey çizgi için aralığı form array'den al
  let x = this.otherDataForm.get('vlines').controls[j].value;
  ctx?.moveTo(x, 0);
  ctx?.lineTo(x, height);
  ctx?.stroke();
 }

 // Canvas'ı sayfaya ekle
 document.body.appendChild(canvas);
 }
}
<div style="display: flex; justify-content: flex-end; align-items: center; height: 100vh;">
  <div class="col-md-4">
  <form (ngSubmit)="onSubmit()" class="form-group mb-3">
  <label for="width" class="mr-2">Dikdörtgenin genişliği (mm):</label>
  <input type="number" id="width" name="width" class="form-control form-control-sm" [(ngModel)]="width">

  <label for="height" class="mr-2">Dikdörtgenin yüksekliği (mm):</label>
  <input type="number" id="height" name="height" class="form-control form-control-sm" [(ngModel)]="height">

  <label for="hlines" class="mr-2">Yatayda kaç çizgi istiyorsunuz?</label>
  <input type="number" id="hlines" name="hlines" class="form-control form-control-sm" [(ngModel)]="hlines">

  <label for="vlines" class="mr-2">Dikeyde kaç çizgi istiyorsunuz?</label>
  <input type="number" id="vlines" name="vlines" class="form-control form-control-sm" [(ngModel)]="vlines">

  <button type="submit" class="btn btn-primary">Gönder</button>

  <!-- Yatay çizgiler için dinamik giriş alanları -->
  <div *ngFor="let hgap of otherDataForm.get('hlines').controls; let i = index">
   <label for="hgap{{i}}">Yatayda {{i+1}}. çizginin dikdörtgenin üst kısmı ile arasında kalan kısım (mm):</label>
   <input type="number" id="hgap{{i}}" name="hgap{{i}}" class="form-control form-control-sm" [formControl]="hgap">
  </div>

  <!-- Dikey çizgiler için dinamik giriş alanları -->
  <div *ngFor="let vgap of otherDataForm.get('vlines').controls; let j = index">
   <label for="vgap{{j}}">Dikeyde {{j+1}}. çizginin dikdörtgenin sol kısmı ile arasında kalan kısım (mm):</label>
   <input type="number" id="vgap{{j}}" name="vgap{{j}}" class="form-control form-control-sm" [formControl]="vgap">
  </div>

  </form>
  </div>
 </div>
